INSERT INTO `t_trace_property_data` VALUES ('1b55ae5279d6445491aa7591ed5b45eb', 'e9e835ce58d04aae88bd7e5a2e92e63d', '5dad4224602449b699d8ef27f7f335bb', '111', '水肥的管理将直接影响到丑桔的产量和品质，所以在施肥的时候，我们要充分考虑到丑橘生长所需要的营养物质，丑橘的一般以有机肥为主，合理配比和使用化肥，在丑橘的幼苗期间，一年至少要追肥5到6次，分为生长肥、促花肥、壮果肥以及养护肥，根据不同的生长期，进行合理的养分配置。接下来就要科学的进行浇水，降雨量较多的时期，要进行及时的排水，天气干旱的时候要保持土壤的湿度，水量要一直保持一种比较平衡的状态。还有就是在果实成长后期，要适量的减少水分，以积累糖分，提高丑橘的口感和品质。', '2019-04-04 16:34:51', '1', NULL, NULL, 'A', '355c1e7b50d5416d8070ac145022d717', 8);
